mapper.update:
UPDATE user_info SET 
		user_name = #{userDto.userName}, 
		user_email = #{userDto.userEmail}, 
		user_phonenum =  #{userDto.phoneNum}
WHERE user_no = #{userNo}

mapper.select:
SELECT user_password
	FROM user_info ui
WHERE user_no = #{userNo}

mapper.update:
UPDATE user_info SET 
	user_password = #{userPassword}
WHERE user_no = #{userNo}
